mapper.update:
UPDATE adinfo
adindex = #{adindex}, adtitle = #{adtitle}, adowner = #{adowner}, telephone = #{telephone}, usestartdate = #{usestartdate}, useenddate = #{useenddate}, playorder = #{playorder}, filename = #{filename}, regtime= DATE_FORMATNOW()+0,'%Y%m%d%H%i%s')
WHERE 1=1 AND adindex = #{adindex}
