mapper.select:
SELECT COUNT(*) AS cnt
FROM member_epilogue A
  WHERE 1=1 AND A.memberid  = #{memberid}

SELECT COUNT(*) AS cnt
FROM member_epilogue A
WHERE A.name  = #{name} AND  A.address  = #{address}

SELECT COUNT(*) AS cnt
FROM member_epilogue A
WHERE A.memberid  = #{memberid}

SELECT IFNULL(A.memberid,'') memberid, IFNULL(A.epiloguename,'') epiloguename, IFNULL(A.epiloguecontent,'') epiloguecontent, DATE_FORMAT( A.regtime,'%Y.%m.%d %H:%i') "regtime"
FROM member_epilogue A
WHERE 1=1 AND A.memberid = #{memberid}

SELECT B.memberid, DATE_FORMAT( B.regtime,'%Y.%m.%d %H:%i') "regtime", B.sequencenum, B.filetype, B.filename
FROM member_epilogue A LEFT OUTER JOIN notice_file B ON A.memberid = B.memberid
WHERE 1=1 AND A.memberid = #{memberid}

mapper.update:
UPDATE member_epilogue
memberid= #{memberid}, epiloguename= #{epiloguename}, epiloguecontent= #{epiloguecontent}, regtime= DATE_FORMAT(NOW()+0,'%Y%m%d%H%i%s')
WHERE 1=1 AND memberid = #{memberid}

mapper.delete:
DELETE FROM member_epilogue
WHERE 1=1
AND memberid = #{memberid}

DELETE FROM notice_file
WHERE 1=1
AND memberid = #{memberid} AND sequencenum = #{sequencenum}

mapper.select:
SELECT COUNT(memberid) cnt
FROM member_epilogue
WHERE memberid = #{memberid}
