mapper.select:
SELECT COUNT(*) AS cnt
FROM health A
  WHERE 1=1 AND A.noticeindex  = #{noticeindex}

SELECT COUNT(*) AS cnt
FROM health A
WHERE A.name  = #{name} AND  A.address  = #{address}

SELECT COUNT(*) AS cnt
FROM health A
WHERE A.noticeindex  = #{noticeindex}

SELECT A.noticeindex, A.noticetitle, A.regid, A.noticecontent, A.searchcount, DATE_FORMAT( A.regtime,'%Y.%m.%d %H:%i') "regtime"
FROM health A
WHERE 1=1 AND A.noticeindex = #{noticeindex}

SELECT B.noticeindex, DATE_FORMAT( B.regtime,'%Y.%m.%d %H:%i') "regtime", B.sequencenum, B.filetype, B.filename
FROM health A LEFT OUTER JOIN notice_file B ON A.noticeindex = B.noticeindex
WHERE 1=1 AND A.noticeindex = #{noticeindex}

mapper.update:
UPDATE health
noticeindex= #{noticeindex}, noticetitle= #{noticetitle}, regid= #{regid}, noticecontent= #{noticecontent}, searchcount= #{searchcount}, regtime= DATE_FORMAT(NOW()+0,'%Y%m%d%H%i%s')
WHERE 1=1 AND noticeindex = #{noticeindex}

mapper.delete:
DELETE FROM health
WHERE 1=1
AND noticeindex = #{noticeindex}

DELETE FROM health_file
WHERE 1=1
AND noticeindex = #{noticeindex} AND sequencenum = #{sequencenum}
