mapper.select:
SELECT COUNT(*) AS cnt
FROM health A
WHERE 1 = 1 AND A.healthindex = #{healthindex}

SELECT COUNT(*) AS cnt
FROM health A
WHERE A.name = #{name} AND A.address = #{address}

SELECT COUNT(*) AS cnt
FROM health A
WHERE A.healthindex = #{healthindex}

mapper.update:
UPDATE health
healthindex= #{healthindex}, healthtitle= #{healthtitle}, regid= #{regid}, healthcontent= #{healthcontent}, searchcount= #{searchcount}, heartcount= #{heartcount}, thumbnailfile= #{thumbnailfile}, regtime= DATE_FORMAT(NOW()+0,'%Y%m%d%H%i%s')
WHERE 1=1 AND healthindex = #{healthindex}

mapper.delete:
DELETE FROM health
WHERE 1 = 1
      AND healthindex = #{healthindex}

DELETE FROM health_file
WHERE 1 = 1
      AND healthindex = #{healthindex} AND sequencenum = #{sequencenum}

DELETE FROM health_file
WHERE 1 = 1
      AND healthindex = #{healthindex}

DELETE FROM health_file
WHERE 1 = 1
      AND healthindex = #{healthindex} AND sequencenum = #{sequencenum}

mapper.select:
select ifnull(max(sequencenum), 0)+1
from health_file
where healthindex = #{healthindex}

mapper.update:
UPDATE health
healthtitle= #{healthtitle}, healthcontent= #{healthcontent},
WHERE 1=1 AND healthindex = #{healthindex}

UPDATE health
thumbnailfile= #{thumbnailfile},
WHERE 1=1 AND healthindex = #{healthindex}

UPDATE health_file
healthindex = #{healthindex}, sequencenum = #{sequencenum}, filename = #{filename}, pictureorder = #{pictureorder}, regtime = #{regtime},
WHERE 1=1 AND healthindex = #{healthindex}
