mapper.select:
SELECT COUNT(*) AS cnt
FROM health A
WHERE 1 = 1 AND A.healthindex = #{healthindex}

SELECT COUNT(*) AS cnt
FROM health A
WHERE A.name = #{name} AND A.address = #{address}

SELECT COUNT(*) AS cnt
FROM health A
WHERE A.healthindex = #{healthindex}

SELECT B.healthindex, DATE_FORMAT(B.regtime, '%Y.%m.%d %H:%i') "regtime", B.sequencenum, B.pictureorder, B.filename
FROM health A LEFT OUTER JOIN health_file B ON A.healthindex = B.healthindex
WHERE 1 = 1 AND A.healthindex = #{healthindex}

mapper.update:
UPDATE health
healthindex= #{healthindex}, healthtitle= #{healthtitle}, regid= #{regid}, healthcontent= #{healthcontent}, searchcount= #{searchcount}, heartcount= #{heartcount}, thumbnailfile= #{thumbnailfile}, regtime= DATE_FORMAT(NOW()+0,'%Y%m%d%H%i%s')
WHERE 1=1 AND healthindex = #{healthindex}

mapper.delete:
DELETE FROM health
WHERE 1 = 1
      AND healthindex = #{healthindex}

DELETE FROM health_file
WHERE 1 = 1
      AND healthindex = #{healthindex} AND sequencenum = #{sequencenum}
